import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useStripe, Elements, PaymentElement, useElements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Sidebar } from "@/components/layout/sidebar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";

// Make sure to call `loadStripe` outside of a component's render to avoid
// recreating the `Stripe` object on every render.
const stripePromise = import.meta.env.VITE_STRIPE_PUBLIC_KEY ? 
  loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY) : 
  Promise.resolve(null);

const SubscribeForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: window.location.origin + '/billing',
      },
    });

    if (error) {
      toast({
        title: "Payment Failed",
        description: error.message,
        variant: "destructive",
      });
    } else {
      toast({
        title: "Payment Successful",
        description: "You are subscribed!",
      });
      setLocation('/billing');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement />
      <div className="flex justify-end space-x-4">
        <Button type="button" variant="outline" onClick={() => setLocation('/billing')} data-testid="button-cancel">
          Cancel
        </Button>
        <Button type="submit" disabled={!stripe || !elements} data-testid="button-subscribe">
          Subscribe
        </Button>
      </div>
    </form>
  );
};

interface SubscriptionPlan {
  id: string;
  name: string;
  price: string;
  documentsLimit: number;
  envelopesLimit: number;
  aiRequestsLimit: number;
  storageLimit: number;
  features: string[];
}

export default function Subscribe() {
  const [clientSecret, setClientSecret] = useState("");
  const [selectedPlan, setSelectedPlan] = useState("professional");
  const [isCreatingSubscription, setIsCreatingSubscription] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  // Fetch subscription plans from API
  const { data: pricingPlans = [], isLoading: plansLoading } = useQuery<SubscriptionPlan[]>({
    queryKey: ["/api/subscription-plans"],
  });

  // Handle missing Stripe configuration FIRST
  if (!import.meta.env.VITE_STRIPE_PUBLIC_KEY) {
    return (
      <div className="min-h-screen flex">
        <Sidebar />
        <main className="flex-1 flex items-center justify-center">
          <Card className="max-w-md mx-auto">
            <CardHeader>
              <CardTitle className="text-center text-destructive">Stripe Not Configured</CardTitle>
            </CardHeader>
            <CardContent className="text-center space-y-4">
              <p className="text-muted-foreground">
                Payment processing is not available. Please contact support to set up your subscription.
              </p>
              <Button variant="outline" onClick={() => setLocation('/billing')}>
                Back to Billing
              </Button>
            </CardContent>
          </Card>
        </main>
      </div>
    );
  }

  const createSubscription = async () => {
    if (!selectedPlan) return;

    setIsCreatingSubscription(true);
    try {
      const response = await apiRequest("POST", "/api/get-or-create-subscription", { planId: selectedPlan });
      const data = await response.json();
      setClientSecret(data.clientSecret);
    } catch (error: any) {
      toast({
        title: "Error",
        description: "Failed to initialize subscription. Please try again.",
        variant: "destructive",
      });
      console.error("Subscription error:", error);
    } finally {
      setIsCreatingSubscription(false);
    }
  };

  if (plansLoading) {
    return (
      <div className="min-h-screen flex">
        <Sidebar />
        <main className="flex-1 flex items-center justify-center">
          <Card className="max-w-md mx-auto">
            <CardHeader>
              <CardTitle className="text-center text-destructive">Stripe Not Configured</CardTitle>
            </CardHeader>
            <CardContent className="text-center space-y-4">
              <p className="text-muted-foreground">
                Payment processing is not available. Please contact support to set up your subscription.
              </p>
              <Button variant="outline" onClick={() => setLocation('/billing')}>
                Back to Billing
              </Button>
            </CardContent>
          </Card>
        </main>
      </div>
    );
  }

  if (plansLoading) {
    return (
      <div className="min-h-screen flex">
        <Sidebar />
        <main className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" aria-label="Loading"/>
            <p className="text-muted-foreground">Loading subscription plans...</p>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex">
      <Sidebar />
      <main className="flex-1 overflow-auto">
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-foreground">Subscribe</h1>
              <p className="text-muted-foreground">Choose the plan that's right for your business</p>
            </div>
            <Button variant="outline" onClick={() => setLocation('/billing')} data-testid="button-back-to-billing">
              Back to Billing
            </Button>
          </div>
        </header>

        <div className="p-6 space-y-8">
          {/* Pricing Plans */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
            {pricingPlans.map((plan, index) => (
              <Card 
                key={plan.id} 
                className={`relative cursor-pointer transition-all hover:shadow-lg ${
                  selectedPlan === plan.id ? 'border-primary border-2 shadow-lg' : 
                  index === 1 ? 'border-primary border-2' : ''
                }`}
                onClick={() => setSelectedPlan(plan.id)}
                data-testid={`card-plan-${plan.id}`}
              >
                {(index === 1 || selectedPlan === plan.id) && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <Badge className="bg-primary text-primary-foreground">
                      {selectedPlan === plan.id ? 'Selected' : 'Most Popular'}
                    </Badge>
                  </div>
                )}
                <CardHeader className="text-center">
                  <CardTitle className="text-lg">{plan.name}</CardTitle>
                  <div className="mt-2">
                    <span className="text-3xl font-bold text-foreground">${plan.price}</span>
                    <span className="text-muted-foreground">/month</span>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <ul className="space-y-3 text-sm">
                    <li className="flex items-center space-x-2">
                      <i className="fas fa-check text-green-600"></i>
                      <span>{plan.documentsLimit} documents/month</span>
                    </li>
                    <li className="flex items-center space-x-2">
                      <i className="fas fa-check text-green-600"></i>
                      <span>{plan.envelopesLimit} envelopes/month</span>
                    </li>
                    <li className="flex items-center space-x-2">
                      <i className="fas fa-check text-green-600"></i>
                      <span>{plan.aiRequestsLimit} AI requests/month</span>
                    </li>
                    <li className="flex items-center space-x-2">
                      <i className="fas fa-check text-green-600"></i>
                      <span>{plan.storageLimit} GB storage</span>
                    </li>
                    {(plan.features || []).map((feature: string, featureIndex: number) => (
                      <li key={featureIndex} className="flex items-center space-x-2">
                        <i className="fas fa-check text-green-600"></i>
                        <span>{feature}</span>
                      </li>
                    ))}
                  </ul>
                  <Button 
                    className={`w-full ${selectedPlan === plan.id ? 'bg-primary text-primary-foreground' : ''}`}
                    variant={selectedPlan === plan.id ? 'default' : 'outline'}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedPlan(plan.id);
                    }}
                    data-testid={`button-select-plan-${plan.id}`}
                  >
                    {selectedPlan === plan.id ? 'Selected' : 'Select Plan'}
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Continue Button */}
          <Card className="max-w-2xl mx-auto">
            <CardContent className="flex items-center justify-between p-6">
              <div>
                <h4 className="font-medium">Ready to subscribe?</h4>
                <p className="text-sm text-muted-foreground">
                  You've selected the {pricingPlans.find(p => p.id === selectedPlan)?.name} plan
                </p>
              </div>
              <Button 
                onClick={createSubscription}
                disabled={isCreatingSubscription}
                data-testid="button-continue-to-payment"
              >
                {isCreatingSubscription ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Preparing...
                  </>
                ) : (
                  'Continue to Payment'
                )}
              </Button>
            </CardContent>
          </Card>

          {/* Payment Form */}
          {clientSecret && selectedPlan && (
            <Card className="max-w-2xl mx-auto">
              <CardHeader>
                <CardTitle>Complete Your Subscription</CardTitle>
                <p className="text-muted-foreground">
                  Enter your payment information to activate your {pricingPlans.find(p => p.id === selectedPlan)?.name} plan
                </p>
              </CardHeader>
              <CardContent>
                {/* Selected Plan Summary */}
                {(() => {
                  const plan = pricingPlans.find(p => p.id === selectedPlan);
                  return plan ? (
                    <div className="bg-muted p-4 rounded-lg mb-6">
                      <div className="flex justify-between items-center">
                        <div>
                          <h4 className="font-medium">{plan.name} Plan</h4>
                          <p className="text-sm text-muted-foreground">
                            {plan.documentsLimit} documents, {plan.envelopesLimit} envelopes, {plan.aiRequestsLimit} AI requests/month
                          </p>
                        </div>
                        <div className="text-right">
                          <div className="text-2xl font-bold">${plan.price}</div>
                          <div className="text-sm text-muted-foreground">per month</div>
                        </div>
                      </div>
                    </div>
                  ) : null;
                })()}

                {/* Make SURE to wrap the form in <Elements> which provides the stripe context. */}
                <Elements stripe={stripePromise} options={{ clientSecret }}>
                  <SubscribeForm />
                </Elements>
              </CardContent>
            </Card>
          )}

          {!clientSecret && selectedPlan && (
            <Card className="max-w-2xl mx-auto">
              <CardContent className="flex items-center justify-center py-8">
                <div className="text-center">
                  <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
                  <p className="text-muted-foreground">Preparing your subscription...</p>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Features Summary */}
          <Card className="max-w-4xl mx-auto">
            <CardHeader>
              <CardTitle className="text-center">What You Get with DocuSign Pro</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
                <div>
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                    <i className="fas fa-brain text-blue-600 text-xl"></i>
                  </div>
                  <h3 className="font-semibold mb-2">AI Document Parsing</h3>
                  <p className="text-sm text-muted-foreground">
                    Automatically extract property details and signature fields from your documents
                  </p>
                </div>
                <div>
                  <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                    <i className="fas fa-file-signature text-green-600 text-xl"></i>
                  </div>
                  <h3 className="font-semibold mb-2">E-Signature Integration</h3>
                  <p className="text-sm text-muted-foreground">
                    Send documents for signature with automated tracking and reminders
                  </p>
                </div>
                <div>
                  <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center mx-auto mb-4">
                    <i className="fas fa-chart-line text-purple-600 text-xl"></i>
                  </div>
                  <h3 className="font-semibold mb-2">Usage Analytics</h3>
                  <p className="text-sm text-muted-foreground">
                    Track performance and manage your subscription with detailed insights
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/upload.tsx.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm, useFieldArray } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { Sidebar } from "@/components/layout/sidebar";
import { DocumentUpload } from "@/components/document-upload";
import { useLocation } from "wouter";
import { apiRequest } from "@/lib/queryClient";

const uploadSchema = z.object({
  documentType: z.string().optional(),
  propertyAddress: z.string().optional(),
  emailSubject: z.string().min(1, "Email subject is required"),
  emailMessage: z.string().optional(),
  recipients: z.array(z.object({
    name: z.string().min(1, "Name is required"),
    email: z.string().email("Valid email is required"),
    phone: z.string().optional(),
    role: z.string().min(1, "Role is required"),
  })).min(1, "At least one recipient is required"),
});

type UploadFormData = z.infer<typeof uploadSchema>;

export default function Upload() {
  const [, setLocation] = useLocation();
  const [uploadedFile, setUploadedFile] = useState<File | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: recipients = [] } = useQuery({
    queryKey: ["/api/recipients"],
  });

  const form = useForm<UploadFormData>({
    resolver: zodResolver(uploadSchema),
    defaultValues: {
      emailSubject: "",
      emailMessage: "",
      recipients: [{ name: "", email: "", phone: "", role: "buyer" }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control: form.control,
    name: "recipients",
  });

  const uploadMutation = useMutation({
    mutationFn: async (data: UploadFormData & { file: File }) => {
      const formData = new FormData();
      formData.append('document', data.file);
      formData.append('emailSubject', data.emailSubject);
      formData.append('emailMessage', data.emailMessage || '');
      formData.append('recipients', JSON.stringify(data.recipients));

      return await apiRequest("POST", "/api/documents/upload", formData);
    },
    onSuccess: () => {
      toast({
        title: "Document uploaded successfully",
        description: "Your document is being processed and will be sent for signatures.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });
      setLocation('/documents');
    },
    onError: (error) => {
      toast({
        title: "Upload failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: UploadFormData) => {
    if (!uploadedFile) {
      toast({
        title: "No file selected",
        description: "Please select a PDF file to upload.",
        variant: "destructive",
      });
      return;
    }

    uploadMutation.mutate({ ...data, file: uploadedFile });
  };

  const addRecipientFromContacts = (recipient: any) => {
    append({
      name: recipient.name,
      email: recipient.email,
      phone: recipient.phone || "",
      role: recipient.role,
    });
  };

  return (
    <div className="min-h-screen flex">
      <Sidebar />
      <main className="flex-1 overflow-auto">
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-foreground">Upload Document</h1>
              <p className="text-muted-foreground">Upload your real estate documents for processing and e-signature</p>
            </div>
          </div>
        </header>

        <div className="p-6 max-w-4xl mx-auto">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
              {/* Upload Area */}
              <Card>
                <CardHeader>
                  <CardTitle>Select Document</CardTitle>
                </CardHeader>
                <CardContent>
                  <DocumentUpload
                    onFileSelect={setUploadedFile}
                    selectedFile={uploadedFile}
                  />
                </CardContent>
              </Card>

              {/* Document Details */}
              <Card>
                <CardHeader>
                  <CardTitle>Document Information</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="documentType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Document Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-document-type">
                                <SelectValue placeholder="Auto-detect with AI" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="auto">Auto-detect with AI</SelectItem>
                              <SelectItem value="purchase_agreement">Purchase Agreement</SelectItem>
                              <SelectItem value="listing_agreement">Listing Agreement</SelectItem>
                              <SelectItem value="disclosure">Disclosure Statement</SelectItem>
                              <SelectItem value="addendum">Addendum</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="propertyAddress"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Property Address</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="Will be extracted automatically" 
                              data-testid="input-property-address"
                              {...field} 
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                  <FormField
                    control={form.control}
                    name="emailSubject"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email Subject Line</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Please sign: [Document Name]" 
                            data-testid="input-email-subject"
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="emailMessage"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email Message</FormLabel>
                        <FormControl>
                          <Textarea 
                            rows={3} 
                            placeholder="Add a personal message to recipients..."
                            data-testid="textarea-email-message"
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>

              {/* Recipients */}
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle>Recipients</CardTitle>
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => append({ name: "", email: "", phone: "", role: "buyer" })}
                      data-testid="button-add-recipient"
                    >
                      <i className="fas fa-plus mr-2"></i>
                      Add Recipient
                    </Button>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {/* Quick Add from Contacts */}
                  {(recipients as any[] || []).length > 0 && (
                    <div className="border border-border rounded-lg p-4">
                      <h4 className="font-medium mb-3">Quick Add from Contacts</h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-2">
                        {(recipients as any[] || []).slice(0, 6).map((recipient: any) => (
                          <Button
                            key={recipient.id}
                            type="button"
                            variant="outline"
                            size="sm"
                            onClick={() => addRecipientFromContacts(recipient)}
                            className="justify-start"
                            data-testid={`button-add-contact-${recipient.id}`}
                          >
                            <i className="fas fa-plus w-4 h-4 mr-2"></i>
                            {recipient.name}
                          </Button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Manual Recipients */}
                  {fields.map((field, index) => (
                    <div key={field.id} className="border border-border rounded-lg p-4">
                      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                        <FormField
                          control={form.control}
                          name={`recipients.${index}.name`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Name</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="Full Name" 
                                  data-testid={`input-recipient-name-${index}`}
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name={`recipients.${index}.email`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email</FormLabel>
                              <FormControl>
                                <Input 
                                  type="email" 
                                  placeholder="email@example.com" 
                                  data-testid={`input-recipient-email-${index}`}
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name={`recipients.${index}.role`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Role</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger data-testid={`select-recipient-role-${index}`}>
                                    <SelectValue />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="buyer">Buyer</SelectItem>
                                  <SelectItem value="seller">Seller</SelectItem>
                                  <SelectItem value="agent">Agent</SelectItem>
                                  <SelectItem value="witness">Witness</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <div className="flex items-end">
                          {fields.length > 1 && (
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={() => remove(index)}
                              data-testid={`button-remove-recipient-${index}`}
                            >
                              <i className="fas fa-trash-alt"></i>
                            </Button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </CardContent>
              </Card>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-4">
                <Button 
                  type="button"
                  variant="outline"
                  onClick={() => setLocation('/documents')}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={uploadMutation.isPending || !uploadedFile}
                  data-testid="button-process-document"
                >
                  {uploadMutation.isPending ? (
                    <>
                      <i className="fas fa-spinner fa-spin mr-2"></i>
                      Processing...
                    </>
                  ) : (
                    'Process Document'
                  )}
                </Button>
              </div>
            </form>
          </Form>
        </div>
      </main>
    </div>
  );
}

client/src/components/layout/sidebar.tsx.content:
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";

interface NavItem {
  id: string;
  label: string;
  icon: string;
  path: string;
}

const navItems: NavItem[] = [
  { id: "dashboard", label: "Dashboard", icon: "fas fa-tachometer-alt", path: "/" },
  { id: "documents", label: "Documents", icon: "fas fa-folder-open", path: "/documents" },
  { id: "upload", label: "Upload", icon: "fas fa-cloud-upload-alt", path: "/upload" },
  { id: "recipients", label: "Recipients", icon: "fas fa-users", path: "/recipients" },
  { id: "billing", label: "Billing", icon: "fas fa-credit-card", path: "/billing" },
];

export function Sidebar() {
  const { user } = useAuth();
  const [location, setLocation] = useLocation();

  const isActive = (path: string) => {
    if (path === "/" && location === "/") return true;
    if (path !== "/" && location.startsWith(path)) return true;
    return false;
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  return (
    <aside className="w-64 bg-card border-r border-border flex flex-col">
      <div className="p-6 border-b border-border">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <i className="fas fa-file-signature text-primary-foreground text-lg"></i>
          </div>
          <div>
            <h1 className="text-xl font-bold text-foreground">DocuSign Pro</h1>
            <p className="text-sm text-muted-foreground">Real Estate Edition</p>
          </div>
        </div>
      </div>

      <nav className="flex-1 p-4 space-y-2">
        {navItems.map((item) => (
          <button
            key={item.id}
            onClick={() => setLocation(item.path)}
            className={`w-full flex items-center space-x-3 px-3 py-2 rounded-md text-sm font-medium transition-colors ${
              isActive(item.path)
                ? "bg-secondary text-secondary-foreground"
                : "text-muted-foreground hover:bg-secondary hover:text-secondary-foreground"
            }`}
            data-testid={`nav-${item.id}`}
          >
            <i className={`${item.icon} w-5`}></i>
            <span>{item.label}</span>
          </button>
        ))}
      </nav>

      <div className="p-4 border-t border-border">
        <div className="flex items-center space-x-3 mb-4">
          {(user as any)?.profileImageUrl ? (
            <img 
              src={(user as any).profileImageUrl} 
              alt="Profile" 
              className="w-10 h-10 rounded-full object-cover"
            />
          ) : (
            <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
              <i className="fas fa-user text-primary"></i>
            </div>
          )}
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-foreground truncate" data-testid="text-user-name">
              {(user as any)?.firstName && (user as any)?.lastName 
                ? `${(user as any).firstName} ${(user as any).lastName}`
                : (user as any)?.email || 'User'
              }
            </p>
            <p className="text-xs text-muted-foreground truncate">
              Real Estate Professional
            </p>
          </div>
        </div>
        <Button 
          variant="ghost" 
          size="sm"
          onClick={handleLogout}
          className="w-full justify-start text-muted-foreground hover:text-foreground"
          data-testid="button-sign-out"
        >
          <i className="fas fa-sign-out-alt mr-2"></i>
          Sign Out
        </Button>
      </div>
    </aside>
  );
}

